mapper.select:
SELECT  FROM
(
SELECT A.*,ROWNUM RN
FROM (
SELECT * FROM GROUPS
WHERE USERID = #{userid}
ORDER BY GID DESC
) A
)
WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT 
FROM GROUPS
WHERE GID = #{gid}
AND USERID = #{userid}

SELECT 
FROM GROUPS
WHERE GNAME = #{gname}
AND USERID = #{userid}

mapper.update:
UPDATE GROUPS
SET GNAME = #{gname}
WHERE GID = #{gid} AND USERID = #{userid}
