mapper.select:
SELECT * FROM
  (
    SELECT A.*,ROWNUM RN
    FROM (
      SELECT C.CID,C.USERID,C.NAME,C.EMAIL,G.GROUPNAME GROUPID FROM CONTACT C
      INNER JOIN GROUPS G
      ON C.GROUPID = G.GID
      WHERE C.USERID = #{userid}
      ORDER BY C.CID DESC
    ) A
  )
WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT 
FROM CONTACT
WHERE CID = #{cid}
AND USERID = #{userid}

SELECT

FROM CONTACT
WHERE NAME = #{name}
AND USERID = #{userid}

SELECT COUNT(*) CID FROM CONTACT
WHERE USERID = #{userid}
AND GROUPID = #{groupid}

mapper.update:
UPDATE CONTACT
SET NAME = #{name}, EMAIL = #{email}, GROUPID = #{groupid}
WHERE USERID = #{userid}
AND CID = #{cid}

mapper.delete:
DELETE FROM CONTACT
WHERE CID = #{cid}
AND USERID = #{userid}
