mapper.select:
SELECT * FROM
(
SELECT A.*,ROWNUM RN
FROM (
SELECT * FROM EMAIL
WHERE USERID = #{userid}
ORDER BY EID DESC
) A
)
WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT 
FROM EMAIL
WHERE EID = #{eid}
AND USERID = #{userid}

mapper.update:
UPDATE EMAIL
SET STATUS= #{status}
WHERE USERID = #{userid}
AND CID = #{cid}

mapper.delete:
DELETE FROM EMAIL
WHERE EID = #{eid}
AND USERID = #{userid}
